export type RSSItem = {
    title: string;
    date: string;
    anchor: string;
    description?: string;
    categories?: string[];
};
export type SavedRssFile = {
    version?: 'v3';
    rssPath?: string;
    filePath?: string;
    updates: RSSItem[];
    orgName: string;
    orgDescription?: string;
    title?: string;
    description?: string;
};
export type SavedRssFileWithContent = SavedRssFile & {
    content?: string | SavedRssFile;
};
export type RSSItemV2 = RSSItem & {
    content?: string;
};
export type SavedRssFileV2 = SavedRssFile & {
    updates: RSSItemV2[];
};
export type RssSnippetFile = {
    path: string;
    content: string;
    importNames?: string[];
};
export type SavedRssFileV4 = {
    version: 'v4';
    rssPath: string;
    orgName: string;
    orgDescription?: string;
    title?: string;
    description?: string;
    filePath: string;
    content: string;
    lineBlame: Record<number, string>;
    snippets?: RssSnippetFile[];
};
